    private void btEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btEditActionPerformed
        try {   
            String sql ="update pasien set name=?, gender=?, address=?, Bloodgroup=? where id=?";
           PreparedStatement stat = conn.prepareStatement(sql);
           stat.setString(1,tfName.getText());
           String gender="";
           if (rbMale.isSelected())
               gender="Laki-Laki";
           else
               gender="Peremuan";
           stat.setString(2, gender);
           
           stat.setString(3, jtAddress.getText());
           stat.setString(4,cbBloodGroup.getSelectedItem().toString());
           stat.setString(5, tfId.getText());
           stat.executeUpdate();
           JOptionPane.showMessageDialog(null, "Data Berhasil diubah");
           clear();
           tfId.requestFocus();
           datatable();
      }catch (SQLException e){
          JOptionPane.showMessageDialog(null, "Data gagal diubah "+e);
      }
    }//GEN-LAST:event_btEditActionPerformed

    private void btDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btDeleteActionPerformed
        // TODO add your handling code here:
        int ok = JOptionPane.showConfirmDialog(null,"hapus","Konfirmasi Dialog",JOptionPane.YES_NO_CANCEL_OPTION);
        if (ok==0){
            String sql ="delete from pasien where id='"+tfId.getText()+"'";
            try {
                PreparedStatement stat = conn.prepareStatement(sql);
                stat.executeUpdate();
                JOptionPane.showMessageDialog(null, "Data berhasil dihapus");
                clear();
                tfId.requestFocus();
                datatable();
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "Data gagal dihapus"+e);
            }
        }
    }//GEN-LAST:event_btDeleteActionPerformed

