    private void btSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btSaveActionPerformed
        // TODO add your handling code here:
        String sql ="insert into pasien values (?,?,?,?,?)";
        try {
           PreparedStatement stat = conn.prepareStatement(sql);
           stat.setString(1,tfId.getText());
           stat.setString(2,tfName.getText());
           
           String gender="";
           if (rbMale.isSelected())
               gender="Laki-Laki";
           else
               gender="Peremuan";
           stat.setString(3, gender);
           
           stat.setString(4, jtAddress.getText());
           stat.setString(5, cbBloodGroup.getSelectedItem().toString());
           
           stat.executeUpdate();
           JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
           clear();
           tfId.requestFocus();
           datatable();
            
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Data gagal disimpan"+e);
        }
    }//GEN-LAST:event_btSaveActionPerformed

    private void jtPasienMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jtPasienMouseClicked
        int bar = jtPasien.getSelectedRow();
        String a = tableModel.getValueAt(bar, 0).toString();
        String b = tableModel.getValueAt(bar, 1).toString();
        String c = tableModel.getValueAt(bar, 2).toString();
        String d = tableModel.getValueAt(bar, 3).toString();
        String e = tableModel.getValueAt(bar, 4).toString();
        
        tfId.setText(a);
        tfName.setText(b);
        
        if (c.equals("Laki-Laki")){
            rbMale.setSelected(true);
            rbFemale.setSelected(false);
        }else{
            rbMale.setSelected(false);
            rbFemale.setSelected(true);
        }
        
        jtAddress.setText(d);
        cbBloodGroup.setSelectedItem(e);
    }//GEN-LAST:event_jtPasienMouseClicked

