    protected void aktif(){
        tfId.setEnabled(true);
        tfName.setEnabled(true);
        jtAddress.setEnabled(true);
        tfId.requestFocus();
    }
    
    protected void clear(){
        tfId.setText("");
        tfName.setText("");
        jtAddress.setText("");
        bgGender.clearSelection();
        cbBloodGroup.setSelectedIndex(0);
        tfSearchField.setText("");
    }
    
    protected void datatable(){
        Object[] Baris ={"No Identitas","Nama Pasien","Jenis Kelamin","Alamat","Golongan Darah"};
        tableModel = new DefaultTableModel(null, Baris);
        jtPasien.setModel(tableModel);
        String sql ="Select * From Pasien";
        try {
            java.sql.Statement stat = conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while (hasil.next()){
                String a = hasil.getString("Id");
                String b = hasil.getString("Name");
                String c = hasil.getString("Gender");
                String d = hasil.getString("Address");
                String e = hasil.getString("BloodGroup");
                
                String[] data = {a,b,c,d,e};
                tableModel.addRow(data);
            }
        } catch (Exception e) {
            System.out.println("Input gagal : "+e);
        }
        }
    
    
        
    
    

